mapper.select:
select count(*)
	from likes
where forum_no = #{forumNo} and liker = #{liker}

mapper.update:
UPDATE forum 
	SET view=view+1 
WHERE forum_no=#{forumNo};

mapper.select:
SELECT count(*)
	FROM forum
where forum_no = #{forumNo} and writer = #{writer}

mapper.update:
UPDATE forum 
	SET status='DELETE'
WHERE forum_no = #{forumNo};

UPDATE forum 
	SET update_date = now()
WHERE forum_no = #{forumNo};

mapper.select:
SELECT count(*)
	FROM comments
where comment_no = #{commentNo} and writer = #{writer}
